/** ---------- map types to either custom SVG or safe MDI names ---------- */
const CUSTOM = {
  shirt: ShirtIcon,
  tshirt: TshirtIcon,
  blouse: BlouseIcon,
  top: TopIcon,
  hoodie: HoodieIcon,
  sweater: SweaterIcon,
  jeans: JeansIcon,
  trousers: TrousersIcon,
  shorts: ShortsIcon,
  skirt: SkirtIcon,
  dress: DressIcon,
  jacket: JacketIcon,
  coat: CoatIcon,
  blazer: BlazerIcon,
  polo: PoloIcon,
  cardigan: CardiganIcon,
  bag: IconBag,
  scarf: IconScarf,
  boots: BootsIcon,
  longsleeve: LongsleeveIcon,
  heels: HeelsIcon,
  sandals: SandalsIcon,
  raincoat: RaincoatIcon,
};

const MDI_SAFE = {
  all: 'wardrobe',
  sneakers: 'shoe-sneaker',
  shoes: 'shoe-formal',
  hat: 'hat-fedora',
  tie: 'tie',
};

/** ---------- Component ---------- */
export default function TypeIcon({ type, color = '#6B7280', size = 20 }) {
  const key = String(type || 'all').toLowerCase();

  // Render custom SVG if we have one
  const Custom = CUSTOM[key];
  if (Custom) return <Custom size={size} color={color} />;

  // Otherwise render a *safe* MaterialCommunity icon
  const name = MDI_SAFE[key] || MDI_SAFE.all;
  return <MaterialCommunityIcons name={name} size={size} color={color} />;
}
